from abc import ABC, abstractmethod

class Animal(ABC):
    def __init__(self, nome: str):
        self.__nome: str = nome

    def apresentar_nome(self):
        print(f"Eu sou um(a) {self.__nome}")

class Leao(Animal):
    def __init__(self, nome: str):
        super().__init__(nome)

    def fazer_som(self):
        print(f"roar")

    def mover(self):
        print("andando")

class Elefante(Animal):
    def __init__(self, nome: str):
        super().__init__(nome)

    def fazer_som(self):
        print("afruuuuu")

    def mover(self):
        print("pisando")

class Cobra(Animal):
    def __init__(self, nome: str):
        super().__init__(nome)

    def fazer_som(self):
        print("pspspspsps")

    def mover(self):
        print("rastejando")

def apresentar(animal: Animal):
    animal.apresentar_nome()
    animal.fazer_som()
    animal.mover()
    print("Tipo:", type (animal).__name__)
    print()

def main():
    animais:list[Animal]=[
        Leao("simba"),
        Elefante("dumbo"),
        Cobra("cascavel")
    ]
    for a in animais:
       apresentar(a)

main()
